create table if not exists sina.author (id BIGINT  AUTO_INCREMENT not null, name varchar(100) not null,url  varchar(500) not null,PRIMARY KEY(id))engine=innodb, charset='utf8';
create table if not exists sina.feed(id BIGINT AUTO_INCREMENT not null,author_id BIGINT,content varchar(500) not null,replies integer,retweets integer,timestamp TIMESTAMP,PRIMARY KEY (id))engine=innodb, charset='utf8';